    public boolean validaCampos() {
        if (jTextFieldTitulo.getText().isBlank() || jTextFieldAutor.getText().isBlank() || jTextFieldEditora.getText().isBlank()) {
            JOptionPane.showMessageDialog(rootPane, " Campos em branco", "ERRO NO CADASTRO", 2);
            this.jTextFieldTitulo.setText("");
            this.jTextFieldAutor.setText("");
            this.jTextFieldEditora.setText("");
            jTextFieldTitulo.requestFocus();
            return true;
        }
        return false;
    }

